import React, { useEffect, useRef } from "react";
import "../components/CSS/Rotate.css";
const Imgrotate = () => {
  useEffect(() => {
    const div = document.querySelector(".text");
    const split = div.innerText;
    const val = split.split("");
    const map = val
      .map(
        (chat, i) =>
          `<span style={{'transform':'rotate(${i * 5}deg)'}}>${chat}<span>`
      )
      .join("");
    div.innerText = map;
    console.log(val);

  }, []);
  
  const ref = useRef();
  return (
    <div className="circle">
      <div className="logo">
        <img src="./Images/My-img.jpg" className="logo" alt="pc" />
      </div>
      <div className="text" ref={ref}>
        <p>shubham chikhale-react web developer-</p>
      </div>
    </div>
  );
};

export default Imgrotate;
