import React from "react";
import { Button, Col, Form, Row } from "react-bootstrap";
import { Icon } from "@iconify/react";
import emailjs from "emailjs-com";

const Contact = () => {
  const sendmail = (e) => {
    e.preventdefault();
    emailjs
      .sendForm(
        "service_6h6k7io",
        "template_6mfh53b",
        e.target,
        "YDLrs830Pb34_YtYM"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };
  return (
    <div id="Contact">
      <div className="box">
        <Row>
          <Col
            l={6}
            xs={12}
            style={{
              padding: "20px",
              margin: "20px",
              borderRadius: "20px",
            }}
            className="border"
          >
            <h2 style={{ textAlign: "center" }}>
              <Icon icon="line-md:email-opened-twotone-alt" color="red" />
              mail me
            </h2>
            <Form onSubmit={sendmail}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>name:</Form.Label>
                <Form.Control
                  type="name"
                  name="name"
                  placeholder="Enter name"
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>email:</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Enter email"
                />
                <Form.Text className="text-muted">
                  We'll never share your email with anyone else.
                </Form.Text>
              </Form.Group>
              <Form.Group controlId="formMessage">
                <Form.Label>Message</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  name="message"
                  placeholder="Enter your message"
                />
              </Form.Group>
              <Button
                style={{ marginTop: "10px" }}
                type="submit"
                className="btn bttnn"
              >
                submit
              </Button>
            </Form>
          </Col>
          <Col
            l={6}
            xs={12}
            style={{
              padding: "20px",
              margin: "20px",
              borderRadius: "20px",
            }}
            className="border"
          >
            <h2 style={{ textAlign: "center" }}>contact me</h2>
            <h3 style={{ letterSpacing: "1px", fontWeight: "500" }}>
              <div className="contact_me">
                <Icon icon="material-symbols:contact-phone" color="green" />
                <span style={{ fontSize: "20px" }}>: 7741086495</span>
              </div>
              <br />
              <div className="contact_me">
                <Icon icon="skill-icons:instagram" color="green" />:
                <span style={{ fontSize: "20px" }}>
                  <a href="https://www.instagram.com/chikuu_9800/">
                    chikuu_9800
                  </a>
                </span>
              </div>
              <br />
              <div className="contact_me">
                <Icon icon="line-md:email-opened-twotone-alt" color="green" />
                <span style={{ fontSize: "20px" }}>
                  : shubhamchikhale4@gmail.com
                </span>
              </div>
            </h3>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default Contact;
